    class cl_sender
    {
        function scm($value)
        {
            $r = $value;
            if ($r == '') $r = 'null';
            else
            {
                $r = str_replace('\\\'', '\'', $r);
                $r = chr(39).str_replace(chr(39), chr(39).chr(39), $r).chr(39);
            }
            return $r;
        }

        function getNewRandomItem($docNum)
        {
            $billNumber = rand(1000, 4000);
            $nit = rand(10000, 90000) . '-' . rand(1, 9);
            $r = null;
            $r = array(
                'DocNum' => $docNum,
                'DocType' => 'FACTURA/RECIBO EN DOS CARÁCTERES',
                'DocDate' => date('Y-m-d'),
                'DocDueDate' => date('Y-m-d'),
                'TaxDate' => date('Y-m-d'),
                'CardCode' => 'CÓDIGO DE PROVEEDOR SAP',
                'NumAtCard' => 'ABC-' . $billNumber,
                'DocCurrency' => 'Q',
                'SalesPersonCode' => '19',
                'U_FacFecha' => date('Y-m-d'),
                'U_FacSerie' => 'ABC',
                'U_FacNum' => $billNumber . '',
                'U_FacNit' => $nit,
                'U_FacNom' => 'SHOP NAME',
                'U_Clase_LibroCV' => '¿S?',
                'U_TIPO_DOCUMENTO' => 'FACTURA'
            );

            for($i = 1; $i <= rand(1, 3); $i++)
            {
                $r['detail'][$i - 1] = array(
                                        'ParentKey' => $docNum,
                                        'LineNum' => $i,
